mapper.select:
SELECT dgm.id, dgm.goods_match_code, dgm.goods_code, dgm.corp_code, dg.goods_image FROM def_goods_match dgm,def_goods dg WHERE dgm.goods_code= dg.goods_code
AND dgm.corp_code=#{corp_code}
ORDER BY dgm.goods_match_code DESC

SELECT dgm.id, dgm.goods_match_code, dgm.goods_code, dgm.corp_code, dg.goods_image FROM def_goods_match dgm,def_goods dg WHERE dgm.goods_code= dg.goods_code
AND dgm.corp_code=#{corp_code}
AND dgm.goods_match_code in (SELECT goods_match_code FROM def_goods_match WHERE goods_code like concat('%',#{search_value},'%')) ORDER BY dgm.goods_match_code DESC

SELECT dgm.*, dg.goods_image FROM def_goods_match dgm,def_goods dg WHERE dgm.goods_code= dg.goods_code
AND dgm.corp_code=#{corp_code}
dgm.goods_match_code = #{goods_match_code}
ORDER BY dgm.goods_match_code DESC
