mapper.select:
select *
from DEF_FUNCTION WHERE FUNCTION_CODE IN (
SELECT FUNCTION_CODE FROM DEF_PRIVILEGE
WHERE MASTER_CODE = #{user_id}
or MASTER_CODE = #{role_code}
or MASTER_CODE = #{group_code}
and enable = 'Y')
and ISACTIVE = 'Y'
order by FUNCTION_CODE

select *
 from DEF_ACTION WHERE ACTION_CODE IN (
 SELECT ACTION_CODE FROM DEF_PRIVILEGE
 WHERE MASTER_CODE = #{user_id}
 or MASTER_CODE = #{role_code}
 or MASTER_CODE = #{group_code}
 and enable = 'Y'
 and FUNCTION_CODE = #{function_code})

select * from DEF_ACTION
where ISACTIVE = 'Y'
and function_code = #{function_code}
