mapper.select:
select *
from DEF_FUNCTION WHERE FUNCTION_CODE IN (
SELECT FUNCTION_CODE FROM DEF_PRIVILEGE
WHERE (MASTER_CODE = #{user_code}
or MASTER_CODE = #{role_code}
or MASTER_CODE = #{group_code})
and enable = 'Y'
and isactive = 'Y')
and ISACTIVE = 'Y'
order by FUNCTION_CODE

select *
 from DEF_ACTION WHERE ACTION_CODE IN (
   SELECT ACTION_CODE
   FROM DEF_PRIVILEGE
   WHERE FUNCTION_CODE = #{function_code}
   AND (MASTER_CODE = #{user_code}
     or MASTER_CODE = #{role_code}
     or MASTER_CODE = #{group_code})
    and enable = 'Y'
 and isactive = 'Y')
 and ISACTIVE = 'Y'
 order by ACTION_CODE

select *
FROM DEF_ACTION a,DEF_FUNCTION b
WHERE a.FUNCTION_CODE = b.FUNCTION_CODE
AND a.ACTION_CODE IN (
SELECT ACTION_CODE FROM DEF_PRIVILEGE
WHERE (MASTER_CODE = #{group_code}
OR MASTER_CODE = #{role_code}

    OR MASTER_CODE = #{user_code}

)
and ISACTIVE = 'Y'
)
AND a.ISACTIVE = 'Y'
AND b.ISACTIVE = 'Y'
order by a.FUNCTION_CODE
