mapper.select:
SELECT g.id ,g.goods_code ,g.goods_name ,g.goods_image ,g.goods_price ,g.goods_time ,g.goods_quarter ,g.goods_wave ,g.match_goods ,g.modified_date ,g.modifier ,g.isactive ,g.created_date ,g.creater ,g.goods_description ,g.corp_code ,g.brand_code ,cp.corp_name ,db.brand_name FROM DEF_GOODS g,DEF_CORP cp,DEF_BRAND db WHERE g.corp_code=cp.corp_code AND g.corp_code=db.corp_code AND g.brand_code=db.brand_code
AND g.corp_code=#{corp_code}
AND (g.goods_code LIKE concat('%',#{search_value},'%') OR g.goods_name LIKE concat('%',#{search_value},'%') OR g.goods_price LIKE concat('%',#{search_value},'%') OR g.goods_time LIKE concat('%',#{search_value},'%') OR g.goods_quarter LIKE concat('%',#{search_value},'%') OR g.goods_wave LIKE concat('%',#{search_value},'%') OR g.goods_description LIKE concat('%',#{search_value},'%') OR cp.corp_name LIKE concat('%',#{search_value},'%') OR db.brand_name like concat('%',#{search_value},'%') )
AND g.isactive=#{isactive}
ORDER BY g.id DESC

SELECT g.id ,g.goods_code ,g.goods_name ,g.goods_image ,g.goods_quarter ,g.goods_wave ,g.match_goods ,g.goods_description ,g.corp_code ,cp.corp_name ,db.brand_name FROM def_goods g,def_corp cp,def_brand db WHERE g.corp_code=cp.corp_code AND g.corp_code=db.corp_code AND g.brand_code=db.brand_code AND g.corp_code=#{corp_code} AND g.goods_code != #{goods_code}
AND (g.goods_code LIKE concat('%',#{search_value},'%') OR g.goods_name LIKE concat('%',#{search_value},'%') )
AND g.isactive=#{isactive} ORDER BY g.id DESC
