SELECT g.id, g.goods_code, g.goods_name, g.goods_image, g.goods_quarter, g.goods_wave, g.goods_description, g.corp_code, cp.corp_name, db.brand_name FROM def_goods g,def_corp cp,def_brand db WHERE g.corp_code=cp.corp_code AND g.corp_code=db.corp_code AND g.brand_code=db.brand_code AND g.corp_code=#{corp_code} AND g.goods_code NOT IN (#{goods_code})
AND g.brand_code = #{brand_code}
AND (g.goods_code LIKE concat('%',#{search_value},'%') OR g.goods_name LIKE concat('%',#{search_value},'%') )
AND g.isactive=#{isactive}
AND cp.isactive = 'Y' AND db.isactive = 'Y' GROUP BY g.id ORDER BY g.id DESC

SELECT g.id, g.goods_code, g.goods_name, g.goods_image, g.goods_price, g.goods_time, g.goods_quarter, g.goods_wave, g.modified_date, g.modifier, g.isactive, g.created_date, g.creater, g.goods_description, g.corp_code, g.brand_code, cp.corp_name, db.brand_name FROM def_goods g,def_corp cp,def_brand db WHERE g.corp_code=cp.corp_code and g.corp_code=db.corp_code and g.brand_code=db.brand_code
AND g.corp_code=#{corp_code}
AND
g.brand_code = #{item}
AND
cp.${entryKey} REGEXP ${entryValue}
db.${entryKey} REGEXP ${entryValue}
g.${entryKey} REGEXP ${entryValue}
1=1
AND cp.isactive = 'Y' AND db.isactive = 'Y' GROUP BY g.id ORDER BY g.id DESC

select g.id, g.goods_code, g.goods_name, g.goods_image, g.goods_price, g.goods_time, g.goods_quarter, g.goods_wave, g.modified_date, g.modifier, g.isactive, g.created_date, g.creater, g.goods_description, g.corp_code, cp.corp_name, g.brand_code, db.brand_name FROM def_goods g,def_corp cp,def_brand db WHERE g.corp_code=cp.corp_code AND g.corp_code=db.corp_code AND g.brand_code=db.brand_code AND g.corp_code=#{corp_code} AND g.goods_code=#{goods_code}
AND g.isactive=#{isactive}
AND cp.isactive = 'Y' AND db.isactive = 'Y'

SELECT g.id, g.goods_code, g.goods_name, g.goods_image, g.goods_price, g.goods_time, g.goods_quarter, g.goods_wave, g.modified_date, g.modifier, g.isactive, g.created_date, g.creater, g.goods_description, g.corp_code, cp.corp_name, g.brand_code, db.brand_name FROM def_goods g,def_corp cp,def_brand db WHERE g.corp_code=cp.corp_code AND g.corp_code=db.corp_code AND g.brand_code=db.brand_code AND g.corp_code=#{corp_code} AND goods_name=#{goods_name}
AND g.isactive=#{isactive}
AND cp.isactive = 'Y' AND db.isactive = 'Y'
