SELECT *
FROM def_group
WHERE group_code = #{group_code}
AND corp_code = #{corp_code}

    AND isactive = #{isactive}

SELECT *
FROM def_group
WHERE group_name = #{group_name}
AND corp_code = #{corp_code}

    AND isactive = #{isactive}

SELECT def_group.id, def_group.group_code, def_group.group_name, def_group.role_code, def_group.corp_code, def_role.role_name, def_corp.corp_name, def_group.remark, def_group.modified_date, def_group.modifier, def_group.created_date, def_group.creater, def_group.isactive FROM def_group,def_role,def_corp WHERE def_group.role_code = def_role.role_code AND def_group.corp_code = def_corp.corp_code
AND def_group.CORP_CODE = #{corp_code}
AND def_group.role_code < #{role_code}
AND (def_group.group_code LIKE concat('%',#{search_value},'%') OR def_group.group_name LIKE concat('%',#{search_value},'%') OR def_role.role_name LIKE concat('%',#{search_value},'%') OR def_corp.corp_name LIKE concat('%',#{search_value},'%') )
AND def_role.isactive = 'Y' AND def_corp.isactive = 'Y' GROUP BY def_group.id ORDER BY def_group.id DESC

SELECT def_group.id, def_group.group_code, def_group.group_name, def_group.role_code, def_group.corp_code, def_role.role_name, def_corp.corp_name, def_group.remark, def_group.modified_date, def_group.modifier, def_group.created_date, def_group.creater, def_group.isactive FROM def_group,def_role,def_corp WHERE def_group.role_code = def_role.role_code AND def_group.corp_code = def_corp.corp_code
AND def_group.corp_code = #{corp_code}
AND def_group.role_code < #{role_code}
AND
def_corp.${entryKey} REGEXP ${entryValue}
def_role.${entryKey} REGEXP ${entryValue}
def_group.${entryKey} REGEXP ${entryValue}
1=1
AND def_role.isactive = 'Y' AND def_corp.isactive = 'Y' GROUP BY def_group.id ORDER BY def_group.id DESC

SELECT * FROM def_group WHERE 1 = 1
AND corp_code = #{corp_code}
AND role_code < #{role_code}
AND isactive = 'Y' GROUP By id ORDER BY id DESC

SELECT *
FROM def_group
WHERE role_code = #{role_code}
AND corp_code = #{corp_code}
GROUP By id
