mapper.select:
SELECT p.id, p.function_code, p.master_code, p.action_code, a.action_name, a.action_show_name FROM def_action a,def_function b,def_privilege p WHERE a.function_code = b.function_code AND a.action_code = p.action_code AND a.function_code = p.function_code AND (master_code = #{group_code} OR master_code = #{role_code}
OR master_code = #{user_code}
) AND p.isactive = 'Y' AND a.isactive = 'Y' AND b.isactive = 'Y' ORDER BY b.show_order

SELECT p.id, p.function_code, p.column_name, p.master_code, a.show_name FROM def_table_manager a,def_function b, def_table_privilege p WHERE a.function_code = b.function_code AND a.function_code = p.function_code AND a.column_name = p.column_name AND (master_code = #{group_code} OR master_code = #{role_code}
OR master_code = #{user_code}
) AND p.isactive = 'Y' AND a.isactive = 'Y' AND b.isactive = 'Y' ORDER BY b.show_order ASC,a.filter_weight ASC

SELECT * FROM def_action a,def_function b,def_privilege p WHERE a.function_code = b.function_code AND a.action_code = p.action_code AND a.function_code = p.function_code AND (master_code = #{group_code} OR master_code = #{role_code}
OR master_code = #{user_code}
) AND p.isactive = 'Y'
AND ( b.module_name LIKE concat('%',#{search_value},'%') OR b.function_name LIKE concat('%',#{search_value},'%') OR a.action_name LIKE concat('%',#{search_value},'%') )
AND a.isactive = 'Y' AND b.isactive = 'Y' GROUP BY p.function_code ORDER BY b.show_order
