mapper.select:
SELECT acc_sign.*, DEF_CORP.CORP_NAME from acc_sign,DEF_CORP where acc_sign.corp_code=DEF_CORP.corp_code AND acc_sign.STATUS !='1'
AND acc_sign.corp_code = #{corp_code}
AND acc_sign.user_code IN ( SELECT DEF_USER.user_code from DEF_USER,DEF_GROUP,DEF_CORP where DEF_USER.group_code = DEF_GROUP.group_code AND DEF_USER.corp_code = DEF_CORP.corp_code AND DEF_USER.corp_code = DEF_GROUP.corp_code AND def_group.isactive = 'Y' AND def_corp.isactive = 'Y'
AND DEF_USER.CORP_CODE = #{corp_code}
AND DEF_GROUP.role_code < #{role_code}
AND
def_user.store_code regexp #{item}
)
AND ( acc_sign.user_code LIKE concat('%', #{search_value},'%') OR acc_sign.USER_NAME LIKE concat('%', #{search_value},'%') OR acc_sign.SIGN_TIME LIKE concat('%', #{search_value},'%') OR DEF_CORP.CORP_NAME LIKE concat('%', #{search_value},'%') )
ORDER BY acc_sign.ID DESC

select * from acc_sign,DEF_CORP where acc_sign.corp_code=DEF_CORP.corp_code AND acc_sign.STATUS !='1' AND acc_sign.user_code=#{user_code} AND acc_sign.corp_code=#{corp_code}
AND ( acc_sign.user_code LIKE concat('%', #{search_value},'%') OR acc_sign.USER_NAME LIKE concat('%', #{search_value},'%') OR acc_sign.SIGN_TIME LIKE concat('%', #{search_value},'%') OR DEF_CORP.CORP_NAME LIKE concat('%', #{search_value},'%') OR acc_sign.ISACTIVE LIKE concat('%', #{search_value},'%') )
ORDER BY acc_sign.ID DESC

mapper.delete:
DELETE FROM acc_sign
WHERE user_code=#{user_code}
AND corp_code = #{corp_code};

mapper.select:
SELECT acc_sign.*, DEF_CORP.CORP_NAME from acc_sign,DEF_CORP where acc_sign.corp_code=DEF_CORP.corp_code AND acc_sign.STATUS !='1'
AND acc_sign.corp_code = #{corp_code}
AND acc_sign.user_code IN ( SELECT DEF_USER.user_code from DEF_USER,DEF_GROUP,DEF_CORP where DEF_USER.group_code = DEF_GROUP.group_code AND DEF_USER.corp_code = DEF_CORP.corp_code AND DEF_USER.corp_code = DEF_GROUP.corp_code AND def_group.isactive = 'Y' AND def_corp.isactive = 'Y'
AND DEF_USER.CORP_CODE = #{corp_code}
AND DEF_GROUP.role_code < #{role_code}
AND
store_code regexp #{item}
) AND
DEF_CORP.${entryKey} LIKE concat('%',#{entryValue},'%')
acc_sign.${entryKey} LIKE concat('%',#{entryValue},'%')
1=1
ORDER BY acc_sign.ID DESC
