mapper.select:
SELECT * FROM def_store_group
WHERE corp_code=#{corp_code}
AND store_group_name=#{store_group_name}

   AND isactive=#{isactive}

SELECT *
FROM def_store_group
WHERE corp_code = #{corp_code}
AND store_group_code = #{store_group_code}

    AND isactive = #{isactive}

SELECT da.*, dc.corp_name FROM def_store_group da,def_corp dc WHERE da.corp_code = dc.corp_code
AND da.corp_code=#{corp_code}
AND da.store_group_code IN
#{item}
AND
dc.${entryKey} REGEXP #{entryValue}
da.${entryKey} REGEXP #{entryValue}
1=1
GROUP By da.id ORDER BY da.id DESC

SELECT store_group_code,store_group_name FROM def_store_group WHERE 1=1 AND corp_code=#{corp_code} AND store_group_code IN
#{item}
AND ISACTIVE = 'Y'

SELECT da.*, dc.corp_name FROM def_store_group da,def_corp dc WHERE da.corp_code = dc.corp_code
AND da.corp_code=#{corp_code}
AND da.store_group_code IN
#{item}
AND (da.store_group_code LIKE concat('%',#{search_value},'%') OR da.store_group_name LIKE concat('%',#{search_value},'%') OR dc.corp_name LIKE concat('%',#{search_value},'%') OR da.modified_date LIKE concat('%',#{search_value},'%') OR da.modifier LIKE concat('%',#{search_value},'%') OR da.isactive LIKE concat('%',#{search_value},'%') )
GROUP BY da.i ORDER BY da.id DESC

SELECT da.*, dc.corp_name FROM def_store_group da,def_corp dc WHERE da.corp_code = dc.corp_code
AND da.corp_code=#{corp_code}
AND da.store_group_code IN
#{item}
AND da.store_group_code IN (SELECT store_group_code from def_store WHERE corp_code = #{corp_code} AND STORE_CODE IN
#{item}
AND isactive ='Y')
AND da.store_group_name LIKE concat('%',#{search_value},'%')
AND da.ISACTIVE ='Y' GROUP By da.id ORDER BY da.id DESC
