SELECT *
FROM def_store
WHERE corp_code = #{corp_code}
AND store_code = #{store_code}

    AND isactive = #{isactive}

SELECT * FROM def_store
where corp_code = #{corp_code}
AND store_id = #{store_id}

    AND isactive = #{isactive}

select def_store.id, store_id, store_code, store_name, def_store.brand_code, flg_tob, def_store.area_code, def_store.corp_code, def_store.modified_date, def_store.modifier, def_store.isactive, def_corp.corp_name FROM def_store,def_corp WHERE def_store.corp_code = def_corp.corp_code AND def_store.corp_code = #{corp_code}
AND
def_store.area_code REGEXP #{item}
AND
def_store.brand_code REGEXP #{item}
AND (store_code like concat('%',#{search_value},'%') OR store_id LIKE concat('%',#{search_value},'%') OR store_name like concat('%',#{search_value},'%') OR def_corp.corp_name like concat('%',#{search_value},'%') )
AND def_store.isactive = #{isactive}
AND def_corp.isactive = 'Y' GROUP BY def_store.id ORDER BY def_store.id DESC

select def_store.id, store_code, store_name, def_store.area_code, def_store.corp_code, def_corp.corp_name from def_store,def_corp WHERE def_store.corp_code = def_corp.corp_code AND def_store.corp_code = #{corp_code}
AND
def_store.area_code regexp #{item}
AND
def_store.brand_code regexp #{item}
and def_store.store_name like concat('%',#{search_value},'%')
AND def_store.isactive = #{isactive}
AND def_corp.isactive = 'Y' GROUP BY def_store.id ORDER BY def_store.id DESC

