AND store_name=#{store_name}

    AND isactive = #{isactive}

SELECT count(1) FROM def_store_ACHV_GOAL
WHERE store_code=#{store_code}
AND corp_code = #{corp_code}

SELECT ds.id, store_id, store_code, store_name, flg_tob, ds.area_code, ds.brand_code, ds.corp_code, ds.modified_date, ds.modifier, ds.isactive, dc.corp_name FROM def_store ds,def_corp dc,def_area da,def_brand db WHERE ds.corp_code = dc.corp_code AND ds.corp_code = da.corp_code AND ds.corp_code = db.corp_code
AND ds.corp_code=#{corp_code}
AND
ds.area_code REGEXP #{item}
AND
ds.brand_code REGEXP #{item}
AND ds.store_code IN
#{item}
AND
dc.${entryKey} REGEXP ${entryValue}
ds.area_code LIKE concat('%§',da.area_code,',%') AND da.${entryKey} REGEXP ${entryValue}
ds.brand_code LIKE concat('%§',db.brand_code,',%') AND db.${entryKey} REGEXP ${entryValue}
ds.${entryKey} REGEXP ${entryValue}
1=1
AND dc.isactive = 'Y' AND da.isactive = 'Y' AND db.isactive = 'Y' GROUP BY ds.id ORDER BY ds.id DESC

SELECT ds.id, store_id, store_code, store_name, flg_tob, ds.area_code, ds.brand_code, ds.corp_code, ds.modified_date, ds.modifier, ds.isactive, dc.corp_name FROM def_store ds,def_corp dc WHERE ds.corp_code = dc.corp_code
AND ds.corp_code=#{corp_code}
AND
ds.area_code REGEXP #{item}
AND
ds.brand_code REGEXP #{item}
AND ds.store_code IN
#{item}
AND
dc.${entryKey} REGEXP ${entryValue}
ds.${entryKey} REGEXP ${entryValue}
1=1
AND dc.isactive = 'Y' GROUP BY ds.id ORDER BY ds.id DESC

