AND (def_store.store_code LIKE concat('%',#{search_value},'%') OR def_store.store_name LIKE concat('%',#{search_value},'%') OR def_corp.corp_name LIKE concat('%',#{search_value},'%') OR def_store.area_code LIKE concat('%',#{search_value},'%') )
AND def_corp.isactive = 'Y' AND def_store.isactive = 'Y' ORDER BY def_store.id DESC

