mapper.select:
select *
from DEF_STORE
where CORP_CODE = #{corp_code}
AND STORE_CODE = #{store_code}

    AND isactive = #{isactive}

select DEF_STORE.id, store_code, store_name, brand_code, flg_tob, DEF_STORE.AREA_CODE, DEF_STORE.corp_code, DEF_STORE.modified_date, DEF_STORE.modifier, DEF_STORE.isactive, DEF_CORP.CORP_NAME, DEF_AREA.AREA_NAME from DEF_STORE,DEF_CORP,DEF_AREA WHERE DEF_STORE.CORP_CODE = DEF_CORP.CORP_CODE AND DEF_STORE.CORP_CODE = DEF_AREA.CORP_CODE AND DEF_STORE.AREA_CODE = DEF_AREA.AREA_CODE AND DEF_STORE.CORP_CODE = #{corp_code} AND DEF_STORE.AREA_CODE IN
#{item}
AND STORE_CODE like concat('%',#{search_value},'%') OR DEF_STORE.ID like concat('%',#{search_value},'%') OR STORE_NAME like concat('%',#{search_value},'%') OR DEF_AREA.AREA_NAME like concat('%',#{search_value},'%') OR DEF_CORP.CORP_NAME like concat('%',#{search_value},'%') OR DEF_STORE.MODIFIED_DATE like concat('%',#{search_value},'%') OR DEF_STORE.MODIFIER like concat('%',#{search_value},'%') OR DEF_STORE.ISACTIVE like concat('%',#{search_value},'%')
AND DEF_STORE.ISACTIVE = #{isactive}
ORDER BY DEF_STORE.id DESC

select DEF_STORE.id, store_code, store_name, brand_code, flg_tob, DEF_STORE.AREA_CODE, DEF_STORE.corp_code, DEF_STORE.modified_date, DEF_STORE.modifier, DEF_STORE.isactive, DEF_CORP.CORP_NAME, DEF_AREA.AREA_NAME from DEF_STORE,DEF_CORP,DEF_AREA WHERE DEF_STORE.CORP_CODE = DEF_CORP.CORP_CODE AND DEF_STORE.CORP_CODE = DEF_AREA.CORP_CODE AND DEF_STORE.AREA_CODE = DEF_AREA.AREA_CODE AND DEF_STORE.STORE_CODE IN
#{item}
AND DEF_STORE.CORP_CODE = #{corp_code}
AND STORE_CODE like concat('%',#{search_value},'%') OR DEF_STORE.ID like concat('%',#{search_value},'%') OR STORE_NAME like concat('%',#{search_value},'%') OR DEF_AREA.AREA_NAME like concat('%',#{search_value},'%') OR DEF_CORP.CORP_NAME like concat('%',#{search_value},'%') OR DEF_STORE.MODIFIED_DATE like concat('%',#{search_value},'%') OR DEF_STORE.MODIFIER like concat('%',#{search_value},'%') OR DEF_STORE.ISACTIVE like concat('%',#{search_value},'%')
AND DEF_STORE.ISACTIVE = #{isactive}
ORDER BY STORE_CODE

SELECT *
FROM DEF_STORE
where CORP_CODE = #{corp_code}

    AND brand_code like #{brand_code}


    AND area_code like #{area_code}

AND ISACTIVE = 'Y'

select count(1) from DEF_STORE_ACHV_GOAL
WHERE store_code=#{store_code}
AND corp_code = #{corp_code}
