#{item}
AND DEF_STORE.CORP_CODE = #{corp_code}
AND (STORE_CODE like concat('%',#{search_value},'%') OR DEF_STORE.ID like concat('%',#{search_value},'%') OR DEF_STORE.STORE_ID like concat('%',#{search_value},'%') OR STORE_NAME like concat('%',#{search_value},'%') OR DEF_AREA.AREA_NAME like concat('%',#{search_value},'%') OR DEF_CORP.CORP_NAME like concat('%',#{search_value},'%') OR DEF_STORE.MODIFIED_DATE like concat('%',#{search_value},'%') OR DEF_STORE.MODIFIER like concat('%',#{search_value},'%') OR DEF_STORE.ISACTIVE like concat('%',#{search_value},'%') )
AND DEF_STORE.ISACTIVE = #{isactive}
AND def_corp.isactive = 'Y' AND def_area.isactive = 'Y' ORDER BY DEF_STORE.id DESC

