SELECT def_store.id, store_code, store_name, def_store.area_code, def_store.corp_code, def_corp.corp_name FROM def_store,def_corp WHERE def_store.corp_code = def_corp.corp_code AND def_store.corp_code = #{corp_code}
AND (def_store.store_code LIKE concat('%',#{search_value},'%') OR def_store.store_name LIKE concat('%',#{search_value},'%') OR def_corp.corp_name LIKE concat('%',#{search_value},'%') OR def_store.area_code LIKE concat('%',#{search_value},'%') )
AND def_corp.isactive = 'Y' AND def_store.isactive = 'Y' ORDER BY def_store.id DESC

SELECT ds.id, store_code, store_name, ds.area_code, ds.corp_code FROM def_store ds,def_corp dc,def_area da WHERE ds.corp_code = dc.corp_code AND ds.corp_code = da.corp_code AND ds.corp_code = #{corp_code}
ds.area_code = #{area_code}
AND ds.isactive = #{isactive}
AND dc.isactive = 'Y' AND da.isactive = 'Y' ORDER BY ds.id DESC

SELECT ds.id, ds.store_code, ds.store_name FROM def_store ds,def_corp dc,def_brand db WHERE ds.corp_code = dc.corp_code AND ds.corp_code = db.corp_code AND ds.corp_code=#{corp_code} AND
ds.brand_code REGEXP #{item}
AND ds.store_name LIKE concat('%',#{search_value},'%')
AND ds.isactive = #{isactive}
AND dc.isactive = 'Y' AND db.isactive = 'Y' ORDER BY ds.id DESC
