SELECT def_store.id, store_id, store_code, store_name, flg_tob, province, city, area, street, def_store.area_code, def_store.brand_code, def_store.corp_code, def_store.modified_date, def_store.modifier, def_store.isactive, def_corp.corp_name, def_store.logo FROM def_store,def_corp WHERE def_store.corp_code = def_corp.corp_code
AND def_store.corp_code = #{corp_code}
AND (store_code LIKE concat('%',#{search_value},'%') OR store_id LIKE concat('%',#{search_value},'%') OR store_name LIKE concat('%',#{search_value},'%') OR def_corp.corp_name LIKE concat('%',#{search_value},'%') )
AND def_store.area_code LIKE concat('%',#{search_area_code},'%')
AND def_store.isactive = #{isactive}
AND def_corp.isactive = 'Y' GROUP BY def_store.id ORDER BY def_store.id DESC

