SELECT ds.id, store_id, store_code, store_name, flg_tob, province, city, area, street, ds.area_code, ds.brand_code, ds.corp_code, ds.modified_date, ds.modifier, ds.isactive, ds.logo, dc.corp_name FROM def_store ds,def_corp dc,def_area da,def_brand db WHERE ds.corp_code = dc.corp_code AND ds.corp_code = da.corp_code AND ds.corp_code = db.corp_code
AND ds.corp_code=#{corp_code}
AND (
ds.area_code REGEXP #{item}
OR def_store.store_code IN
#{item}
)
AND
ds.brand_code REGEXP #{item}
AND ds.store_code IN
#{item}
AND
dc.${entryKey} REGEXP ${entryValue}
ds.area_code LIKE concat('%§',da.area_code,',%') AND da.${entryKey} REGEXP ${entryValue}
ds.brand_code LIKE concat('%§',db.brand_code,',%') AND db.${entryKey} REGEXP ${entryValue}
ds.${entryKey} REGEXP ${entryValue}
1=1
AND ds.isactive = #{isactive}
AND dc.isactive = 'Y' AND da.isactive = 'Y' AND db.isactive = 'Y' GROUP BY ds.id ORDER BY ds.id DESC

SELECT ds.id, store_id, store_code, store_name, flg_tob, province, city, area, street, ds.area_code, ds.brand_code, ds.corp_code, ds.modified_date, ds.modifier, ds.isactive, ds.logo, dc.corp_name FROM def_store ds,def_corp dc WHERE ds.corp_code = dc.corp_code
