mapper.select:
SELECT ua.id, u.user_code, u.user_name, ua.store_code, ua.corp_code, ua.user_target, ua.target_type, ua.target_time, ua.modified_date, ua.modifier, ua.created_date, ua.creater, ua.isactive, s.store_name, c.corp_name FROM def_user_achv_goal ua, def_store s, def_corp c, def_user u, def_group g WHERE ua.corp_code = u.corp_code AND ua.corp_code = c.corp_code AND ua.corp_code = g.corp_code AND ua.corp_code = s.corp_code AND ua.store_code=s.store_code AND ua.user_code = u.user_code AND u.group_code = g.group_code AND ua.corp_code=#{corp_code} AND g.role_code < #{role_code}
AND ua.store_code IN
#{item}
AND (u.user_name LIKE concat('%',#{search_value},'%') OR ua.user_code LIKE concat('%',#{search_value},'%') OR s.store_name LIKE concat('%',#{search_value},'%') OR c.corp_name LIKE concat('%',#{search_value},'%') OR ua.user_target LIKE concat('%',#{search_value},'%') OR ua.target_type LIKE concat('%',#{search_value},'%') OR ua.target_time LIKE concat('%',#{search_value},'%') )
GROUP BY ua.id ORDER BY ua.id DESC

mapper.delete:
DELETE FROM def_user_achv_goal
WHERE corp_code = #{corp_code}
AND store_code = #{store_code}
AND user_code = #{user_code}

mapper.select:
SELECT ua.id, u.user_code, u.user_name, ua.store_code, ua.corp_code, ua.user_target, ua.target_type, ua.target_time, ua.modified_date, ua.modifier, ua.created_date, ua.creater, ua.isactive, s.store_name, c.corp_name FROM def_user_achv_goal ua, def_store s, def_corp c, def_user u, def_group g WHERE ua.corp_code=s.corp_code AND ua.corp_code = u.corp_code AND ua.corp_code = c.corp_code AND ua.corp_code=u.corp_code AND ua.corp_code=g.corp_code AND ua.store_code=s.store_code AND ua.user_code = u.user_code AND u.group_code = g.group_code
AND ua.corp_code=#{corp_code}
AND g.role_code < #{role_code}
AND ua.store_code IN
#{item}
AND
u.${entryKey} REGEXP ${entryValue}
s.${entryKey} REGEXP ${entryValue}
c.${entryKey} REGEXP ${entryValue}
ua.${entryKey} REGEXP ${entryValue}
1=1
GROUP BY ua.id ORDER BY ua.id DESC

SELECT count(*)
FROM
def_user_achv_goal duag
WHERE
 duag.corp_code=#{corp_code}
AND duag.user_code=#{user_code}
AND duag.target_type=#{target_type}
AND  duag.target_time=#{target_time}
AND  duag.isactive=#{isactive}
AND  duag.store_code=#{store_code}
