SELECT def_user.id, user_code, user_name, phone, def_user.ISACTIVE FROM def_user,def_group,def_corp WHERE def_user.group_code = def_group.group_code AND def_user.corp_code = def_corp.corp_code AND def_user.corp_code = def_group.corp_code AND def_user.CORP_CODE = #{corp_code} AND def_group.role_code >= #{role_code} AND
store_code regexp #{item}
or user_code in (select user_code from def_user,DEF_GROUP WHERE def_user.corp_code=#{corp_code} and DEF_USER.group_code = DEF_GROUP.group_code and DEF_GROUP.role_code='R4000' and
area_code REGEXP #{item}
)
AND def_corp.isactive = 'Y' AND def_group.isactive = 'Y' GROUP BY def_user.user_code HAVING 1=1
AND USER_NAME LIKE concat('%',#{search_value},'%')
AND def_user.ISACTIVE='Y' ORDER BY def_user.id DESC

SELECT *
FROM def_user
WHERE user_code = #{user_code}
AND corp_code = #{corp_code}

    AND isactive = #{isactive}

SELECT * FROM def_user
WHERE email = #{email}
AND isactive = 'Y'

SELECT def_user.id, user_code, user_name, sex, avatar, phone, email, birthday, password, def_user.group_code, def_user.modified_date, def_user.modifier, def_user.created_date, def_user.creater, def_user.isactive, def_group.group_name, def_group.role_code FROM def_user,def_group,def_corp WHERE def_user.group_code = def_group.group_code AND def_user.corp_code = def_group.corp_code AND def_user.corp_code = def_corp.corp_code AND def_user.corp_code = #{corp_code}
AND def_user.store_code regexp #{store_code}
AND def_user.area_code regexp #{area_code}
AND def_group.role_code < #{role_code}
AND def_user.isactive = #{isactive}
AND def_group.isactive = 'Y' AND def_corp.isactive = 'Y' GROUP BY def_user.id ORDER BY def_user.id DESC

SELECT def_user.id, user_code, user_name, sex, avatar, phone, email, birthday, position, store_code, def_user.group_code, def_user.corp_code, def_user.area_code, login_time_recently, def_user.modified_date, def_user.modifier, def_user.created_date, def_user.creater, def_user.isactive, def_group.group_name, def_group.role_code, def_corp.corp_name FROM def_user,def_group,def_corp WHERE def_user.group_code = def_group.group_code AND def_user.corp_code = def_corp.corp_code
AND def_user.CORP_CODE = #{corp_code}
AND def_user.group_code = #{group_code}
AND ( user_name LIKE concat('%',#{search_value},'%') OR user_code LIKE concat('%',#{search_value},'%') OR phone LIKE concat('%',#{search_value},'%') OR SEX LIKE concat('%',#{search_value},'%') )
AND def_group.isactive = 'Y' AND def_corp.isactive = 'Y' GROUP BY def_user.id ORDER BY def_user.ID DESC
