SELECT def_user.id, user_id, user_code, user_name, sex, def_user.avatar, phone, email, birthday, password, def_user.group_code, def_user.modified_date, def_user.modifier, def_user.created_date, def_user.creater, def_user.isactive, def_user.isonline, def_user.position, def_group.group_name, def_group.role_code FROM def_user,def_group,def_corp WHERE def_user.group_code = def_group.group_code AND def_user.corp_code = def_group.corp_code AND def_user.corp_code = def_corp.corp_code AND def_user.corp_code = #{corp_code}
AND def_user.store_code regexp #{store_code}
AND def_user.area_code regexp #{area_code}
AND def_group.role_code < #{role_code}
AND def_user.isactive = #{isactive}
AND def_group.isactive = 'Y' AND def_corp.isactive = 'Y' GROUP BY def_user.id ORDER BY def_user.id DESC

