mapper.select:
SELECT def_user.id, user_code, user_name, sex, avatar, phone, email, birthday, password, login_time_recently, def_user.group_code, def_user.modified_date, def_user.modifier, def_user.created_date, def_user.creater, def_user.isactive, def_group.group_name FROM def_user,def_group,def_corp WHERE def_user.group_code = def_group.group_code AND def_user.corp_code = def_group.corp_code AND def_user.corp_code = def_corp.corp_code AND def_user.corp_code = #{corp_code}
AND def_user.store_code regexp #{store_code}
AND def_user.area_code regexp #{area_code}
AND def_group.role_code < #{role_code} ORDER BY def_user.id DESC

SELECT def_user.id, user_code, user_name, sex, avatar, phone, email, birthday, store_code, def_user.group_code, def_user.corp_code, def_user.area_code, login_time_recently, def_user.modified_date, def_user.modifier, def_user.created_date, def_user.creater, def_user.isactive, def_group.group_name, def_corp.corp_name FROM def_user,def_group,def_corp WHERE def_user.group_code = def_group.group_code AND def_user.corp_code = def_corp.corp_code
AND def_user.CORP_CODE = #{corp_code}
AND def_user.group_code = #{group_code}
ORDER BY def_user.ID DESC

select du.*, dg.group_name, dc.corp_name from def_user du, def_corp dc, def_group dg where du.corp_code=dc.corp_code and du.corp_code=dg.corp_code and du.group_code=dg.group_code
and du.corp_code=#{corp_code}
and dg.role_code <#{role_code}
AND
store_code regexp #{item}
AND
dc.${entryKey} LIKE concat('%',#{entryValue},'%')
dg.${entryKey} LIKE concat('%',#{entryValue},'%')
du.${entryKey} LIKE concat('%',#{entryValue},'%')
ORDER BY du.id DESC
