mapper.select:
SELECT def_vip_card_type.id, def_vip_card_type.corp_code, def_vip_card_type.vip_card_type_code, def_vip_card_type.vip_card_type_name, def_vip_card_type.degree, def_corp.corp_name, def_vip_card_type.created_date, def_vip_card_type.modified_date, def_vip_card_type.modifier, def_vip_card_type.creater, def_vip_card_type.isactive FROM def_vip_card_type,def_corp WHERE def_vip_card_type.corp_code=def_corp.corp_code
AND def_vip_card_type.corp_code = #{corp_code}
AND
def_corp.${entryKey} REGEXP ${entryValue}
def_vip_card_type.${entryKey} REGEXP ${entryValue}
1=1
GROUP BY def_vip_card_type.id ORDER BY def_vip_card_type.id DESC

SELECT def_vip_card_type.id, def_vip_card_type.corp_code, def_vip_card_type.vip_card_type_code, def_vip_card_type.vip_card_type_name, def_vip_card_type.degree, def_corp.corp_name, def_vip_card_type.created_date, def_vip_card_type.modified_date, def_vip_card_type.modifier, def_vip_card_type.creater, def_vip_card_type.isactive FROM def_vip_card_type,def_corp WHERE def_vip_card_type.corp_code=def_corp.corp_code
AND def_vip_card_type.CORP_CODE = #{corp_code}
AND def_vip_card_type.isactive= #{isactive} AND def_vip_card_type.vip_card_type_code = #{vip_card_type_code}

SELECT def_vip_card_type.id, def_vip_card_type.corp_code, def_vip_card_type.vip_card_type_code, def_vip_card_type.vip_card_type_name, def_corp.corp_name, def_vip_card_type.created_date, def_vip_card_type.modified_date, def_vip_card_type.modifier, def_vip_card_type.creater, def_vip_card_type.isactive FROM def_vip_card_type,def_corp WHERE def_vip_card_type.corp_code=def_corp.corp_code
AND def_vip_card_type.CORP_CODE = #{corp_code}
AND def_vip_card_type.isactive= #{isactive} AND def_vip_card_type.vip_card_type_name = #{vip_card_type_name}

SELECT def_vip_card_type.id, def_vip_card_type.corp_code, def_vip_card_type.vip_card_type_code, def_vip_card_type.vip_card_type_name FROM def_vip_card_type,def_corp WHERE def_vip_card_type.corp_code = def_corp.corp_code
AND def_vip_card_type.corp_code=#{corp_code}
AND def_vip_card_type.isactive = #{isactive}
